        __ERC1155_init(uri_);
        __ERC1155Burnable_init();
        __AccessControl_init();
    }

    function __ERC1155Owl_init_unchained(address _admin) internal onlyInitializing {
        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
